import React, {Component} from "react"

class Grid extends Component{
    constructor(props){
        super(props);



        this.blackdots=this.blackdots.bind(this);

        this.state={
            arr:[]
        }
    }

    blackdots() {
        console.log(this.props.dots);

    }

    componentWillMount(){
        let som=this.props.dots;
        for (let i=0; i<som.size();i++){
            let x = this.props.dots[i][0];
            let y = this.props.dots[i][1];

            // this.setState({
            //     arr[x][y]:
            // })

            this.state.arr[x][y]= <div className="dot"></div>;
        }
    }





    render(){

        this.blackdots();

        // Fixed!!!
        var size = 40;
        this.state.arr = new Array(size);

        for (let i=0;i<size;i++){
            this.state.arr[i] = new Array(size);
        }

        for (let i=0;i<size;i++){
            for (let j=0;j<size;j++){
                this.state.arr[i][j]= <div className="block"></div>;
            }
        }


        // Fixed!!!




        return(
            <div>
                {this.state.arr.map((row) => {
                    return (
                        <div className="row">
                            {row}
                        </div>
                    )
                })}
            </div>
        )
    }
}


