import React, {Component} from "react"
import Snake from "./Snake";
import Food from "./Food"
import Grid from "./Grid"

class App extends Component{
    constructor(props){
        super(props);



        this.state={
            arr:[[]]
        }

        this.snakeMove=this.snakeMove.bind(this);
        this.snakeUpdate=this.snakeUpdate.bind(this);

        this.moveUp=this.moveUp.bind(this);
        this.moveRight=this.moveRight.bind(this);
        this.moveDown=this.moveDown.bind(this);
        this.moveLeft=this.moveLeft.bind(this);
        this.eventHandler=this.eventHandler.bind(this);

        this.setArr=this.setArr.bind(this);
    }

    setArr(array){
        this.setState({
            arr: array
        })
    }

    snakeMove() {

        switch (this.state.direction){
            case "UP":
                this.moveUp();
                break;
            case "RIGHT":
                this.moveRight();
                break;
            case "DOWN":
                this.moveDown();
                break;
            case "LEFT":
                this.moveLeft();
                break;
        }            
    }

    moveRight(){
        let tempArr=this.state.dots;
        tempArr.shift();
        tempArr.push([tempArr[tempArr.length-1][0]+1,tempArr[tempArr.length-1][1]]);

        this.state.dots=tempArr;
    }

    moveDown(){
        let tempArr=this.state.dots;
        tempArr.shift();
        tempArr.push([tempArr[tempArr.length-1][0],tempArr[tempArr.length-1][1]+1]);

        this.state.dots=tempArr;
    }

    moveLeft(){
        let tempArr=this.state.dots;
        tempArr.shift();
        tempArr.push([tempArr[tempArr.length-1][0]-1,tempArr[tempArr.length-1][1]]);

        this.state.dots=tempArr;
    }

    moveUp(){
        let tempArr=this.state.dots;
        tempArr.shift();
        tempArr.push([tempArr[tempArr.length-1][0],tempArr[tempArr.length-1][1]-1]);

        this.state.dots=tempArr;
    }





    render(){
        this.state.directionUpdate=true;
        // Fixed!!!
        var size = 40;
        this.state.arr = new Array(size);

        for (let i=0;i<size;i++){
            this.state.arr[i] = new Array(size);
        }

        for (let i=0;i<size;i++){
            for (let j=0;j<size;j++){
                this.state.arr[i][j]= <div className="block"></div>;
            }
        }
        // Fixed!!!

        this.snakeUpdate(); 


        return(

            <div className="container">
                <Grid arr={this.state.arr}/>
                <Snake arr={this.state.arr}
                    setArr={this.setArr}/>
                {/* <Food /> */}
            </div>

        )
    }
}


