import React, {Component} from "react"

class Grid extends Component{
    constructor(props){
        super(props);


        this.state={
            arr:[[]],
            direction: "RIGHT"
        }

        this.snakeMove=this.snakeMove.bind(this);

    }

    snakeMove() {

        switch (direction){
            case "UP":
                this.moveup();
                break;
            case "RIGHT":
                this.moveright();
                break;
            case "DOWN":
                this.movedown();
                break;
            case "LEFT":
                this.moveleft();
                break;
        }            


        if (direction==="UP"){
            this.moveup();
        }
        else if (direction==="UP"){
            this.moveup();
        }
        else if (direction==="UP"){
            this.moveup();
        }
        if (direction==="UP"){
            this.moveup();
        }
    }

    componentDidMount(){
        setInterval(this.snakeMove,1000);
    }



    render(){

        this.snakeMove();

        // Fixed!!!
        var size = 40;
        this.state.arr = new Array(size);

        for (let i=0;i<size;i++){
            this.state.arr[i] = new Array(size);
        }

        for (let i=0;i<size;i++){
            for (let j=0;j<size;j++){
                this.state.arr[i][j]= <div className="block"></div>;
            }
        }
        // Fixed!!!

        // Snake update
        let som=this.props.dots;
        for (let i=0; i<som.length;i++){
            let x = this.props.dots[i][0];
            let y = this.props.dots[i][1];

            // this.setState({
            //     arr[x][y]:
            // })

            this.state.arr[x][y]= <div className="dot"></div>;
        }
        // Snake update

        return(
            <div>
                {this.state.arr.map((row) => {
                    return (
                        <div className="row">
                            {row}
                        </div>
                    )
                })}
            </div>
        )
    }
}


