import React, {Component} from "react"


class Snake extends Component{

    constructor(props){
        super(props);

        this.state={
            direction: "RIGHT",
            directionUpdate: true
        }

        this.snakeMove=this.snakeMove.bind(this);
        this.snakeUpdate=this.snakeUpdate.bind(this);

        this.moveUp=this.moveUp.bind(this);
        this.moveRight=this.moveRight.bind(this);
        this.moveDown=this.moveDown.bind(this);
        this.moveLeft=this.moveLeft.bind(this);

        this.eventHandler=this.eventHandler.bind(this);
    }

    snakeUpdate(){
       // Snake update
       let some=this.props.arr;
       for (let i=0; i<this.state.dots.length;i++){
           let x = this.props.dots[i][0];
           let y = this.props.dots[i][1];

           some[x][y]= <div className="dot"></div>;
       }
       // Snake 
       this.props.setArr(some);
   }

   snakeMove() {

    switch (this.state.direction){
        case "UP":
            this.moveUp();
            break;
        case "RIGHT":
            this.moveRight();
            break;
        case "DOWN":
            this.moveDown();
            break;
        case "LEFT":
            this.moveLeft();
            break;
    }           
}

moveRight(){
    let tempArr=this.props.dots;
    tempArr.shift();
    tempArr.push([tempArr[tempArr.length-1][0]+1,tempArr[tempArr.length-1][1]]);

    this.props.setDots(tempArr);
}

moveDown(){
    let tempArr=this.state.dots;
    tempArr.shift();
    tempArr.push([tempArr[tempArr.length-1][0],tempArr[tempArr.length-1][1]+1]);

    this.state.dots=tempArr;
}

moveLeft(){
    let tempArr=this.state.dots;
    tempArr.shift();
    tempArr.push([tempArr[tempArr.length-1][0]-1,tempArr[tempArr.length-1][1]]);

    this.state.dots=tempArr;
}

moveUp(){
    let tempArr=this.state.dots;
    tempArr.shift();
    tempArr.push([tempArr[tempArr.length-1][0],tempArr[tempArr.length-1][1]-1]);

    this.state.dots=tempArr;
}

   eventHandler(e){
    console.log("Some key Pressed!!!!");
    console.log(e.code);

    if(!this.state.directionUpdate) return;

    switch(e.code){
        case "ArrowRight":
            if (this.state.direction==="RIGHT" || this.state.direction==="LEFT" ) return;
            this.state.direction = "RIGHT";
            break;
        case "ArrowDown":
            if (this.state.direction==="DOWN" || this.state.direction==="UP" ) return;
            this.state.direction = "DOWN";
            break;
        case "ArrowLeft":
            if (this.state.direction==="RIGHT" || this.state.direction==="LEFT" ) return;
            this.state.direction = "LEFT";
            break;
        case "ArrowUp":
            if (this.state.direction==="DOWN" || this.state.direction==="UP" ) return;
            this.state.direction = "UP";
            break;
        }
        this.state.directionUpdate=false;
    }

    componentDidMount(){   
        // console.log(this.props.dots);
        // this.snakeMove();
        setInterval(this.snakeMove,1000);
        window.addEventListener("keydown",this.eventHandler);
    }

    componentWillUnmount(){
        window.removeEventListener("keydown",this.eventHandler);
    }

    render(){

        return null;
    }



}

export default Snake;
