import React, {Component} from "react"
import { findRenderedDOMComponentWithClass } from "react-dom/cjs/react-dom-test-utils.production.min";

class Grid extends Component{
    constructor(props){
        super(props);



        this.state={
            arr:[[]],
            direction: "RIGHT",
            directionUpdate: true
        }

        this.snakeMove=this.snakeMove.bind(this);
        this.snakeUpdate=this.snakeUpdate.bind(this);

        this.moveUp=this.moveUp.bind(this);
        this.moveRight=this.moveRight.bind(this);
        this.moveDown=this.moveDown.bind(this);
        this.moveLeft=this.moveLeft.bind(this);
        this.eventHandler=this.eventHandler.bind(this);
    }

    snakeMove() {

        switch (this.state.direction){
            case "UP":
                this.moveUp();
                break;
            case "RIGHT":
                this.moveRight();
                break;
            case "DOWN":
                this.moveDown();
                break;
            case "LEFT":
                this.moveLeft();
                break;
        }            
    }

    moveRight(){
        let tempArr=this.props.dots;
        tempArr.shift();
        tempArr.push([tempArr[tempArr.length-1][0]+1,tempArr[tempArr.length-1][1]]);

        this.props.setDots(tempArr);
    }

    moveDown(){
        let tempArr=this.props.dots;
        tempArr.shift();
        tempArr.push([tempArr[tempArr.length-1][0],tempArr[tempArr.length-1][1]+1]);

        this.props.setDots(tempArr);
    }

    moveLeft(){
        let tempArr=this.props.dots;
        tempArr.shift();
        tempArr.push([tempArr[tempArr.length-1][0]-1,tempArr[tempArr.length-1][1]]);

        this.props.setDots(tempArr);
    }

    moveUp(){
        let tempArr=this.props.dots;
        tempArr.shift();
        tempArr.push([tempArr[tempArr.length-1][0],tempArr[tempArr.length-1][1]-1]);

        this.props.setDots(tempArr);
    }

   snakeUpdate(){
       // Snake update
       let som=this.props.dots;
       for (let i=0; i<som.length;i++){
           let x = this.props.dots[i][0];
           let y = this.props.dots[i][1];

           this.state.arr[x][y]= <div className="dot"></div>;
       }
       // Snake update
   }

   eventHandler(e){
        console.log("Some key Pressed!!!!");
        console.log(e.code);

        if(this.directionUpdate) return;

        switch(e.code){
            case "ArrowRight":
                if (this.state.direction==="RIGHT" || this.state.direction==="LEFT" ) return;
                this.state.direction = "RIGHT";
                break;
            case "ArrowDown":
                if (this.state.direction==="DOWN" || this.state.direction==="UP" ) return;
                this.state.direction = "DOWN";
                break;
            case "ArrowLeft":
                if (this.state.direction==="RIGHT" || this.state.direction==="LEFT" ) return;
                this.state.direction = "LEFT";
                break;
            case "ArrowUp":
                if (this.state.direction==="DOWN" || this.state.direction==="UP" ) return;
                this.state.direction = "UP";
                break;
        }
        this.directionUpdate=false;
   }

    componentDidMount(){   
        this.snakeUpdate();
        // console.log(this.props.dots);
        // this.snakeMove();
        setInterval(this.snakeMove,1000);
        window.addEventListener("keydown",this.eventHandler);
    }


    render(){
        this.state.directionUpdate=true;
        // Fixed!!!
        var size = 40;
        this.state.arr = new Array(size);

        for (let i=0;i<size;i++){
            this.state.arr[i] = new Array(size);
        }

        for (let i=0;i<size;i++){
            for (let j=0;j<size;j++){
                this.state.arr[i][j]= <div className="block"></div>;
            }
        }
        // Fixed!!!

        this.snakeUpdate(); 


        return(
            <div>
                {this.state.arr.map((row) => {
                    return (
                        <div className="row">
                            {row}
                        </div>
                    )
                })}
            </div>
        )
    }
}


